import React, { useState } from 'react';
import { Container, Card, ListGroup, ListGroupItem, Button, Form, Col, Row} from 'react-bootstrap';
import { v4 } from 'uuid';
import Rectangle from 'react-rectangle';
import Draggable from 'react-draggable';
import 'bootstrap/dist/css/bootstrap.min.css';
import './style.css';


const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');

    const [isShown, setIsShown] = useState(false);


    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd(event) {
        if (!text){
            return;
        }
        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');
    }


    function Note(props) {
        return (
        <div 
            variant = 'light' 
            className='rounded-pill' 
            onMouseEnter={() => setIsShown(true)}
            onMouseLeave={() => setIsShown(false)}
        >
            {props.intext}
            {isShown && (
                <svg xmlns="http://www.w3.org/2000/svg" onClick={() => props.deleteNote(props.id)} class="icon icon-tabler icon-tabler-trash btn-delete" width="24" height="24" viewBox="0 0 24 24" stroke-width="1.5" stroke="#FF5B5B" fill="none" stroke-linecap="round" stroke-linejoin="round">
                    <path stroke="none" d="M0 0h24v24H0z" fill="none"/>
                    <line x1="4" y1="7" x2="20" y2="7" />
                    <line x1="10" y1="11" x2="10" y2="17" />
                    <line x1="14" y1="11" x2="14" y2="17" />
                    <path d="M5 7l1 12a2 2 0 0 0 2 2h8a2 2 0 0 0 2 -2l1 -12" />
                    <path d="M9 7v-3a1 1 0 0 1 1 -1h4a1 1 0 0 1 1 1v3" />
                </svg>
                // <Button 
                //     className="btn-delete" 
                //     variant="danger" 
                //     size ="sm" 
                //     onClick={() => props.deleteNote(props.id)}
                // > X </Button>
            )}
        </div>
        )
    }


    const deleteNote = (id) => {
        setNote(notes.filter((note) => note.id !== id))
    }


    return ( 
        <>
            <div className="main-header-text"> 
                <p>Letâ€™s find our ikigai! <br></br> Start by adding activites or values you are currently doing into each of these four quadrants.
                <br></br>Feel free to add as many as you can think of! 
                </p> 
            </div>
            <div className="container">
                <div className="rectangle-container">
                    <div className="text-center">
                        <h3 className="dark-blue">What the world <br></br><b>NEEDS</b></h3>
                        <Container className="pill-container">
                            {notes.map((element) => <Note key = {element.id.toString()} intext={element.intext} id={element.id} deleteNote={deleteNote}></Note>)}
                        </Container>

                        <Container>
                            <Form.Control className='form rounded-pill' value={text} onChange={handleChange} onBlur={handleAdd}/>
                        </Container>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Far;
