import React, { useState } from 'react';
import { Container, Card, ListGroup, ListGroupItem, Button, Form, OverlayTrigger, Tooltip} from 'react-bootstrap';
import { v4 } from 'uuid';
import Rectangle from 'react-rectangle';
import Draggable from 'react-draggable';
import 'bootstrap/dist/css/bootstrap.min.css';
import './style.css';
import Trash from './components/trash.js'
import logo from './images/logo.png'; //logo path


const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');

    const [isShown, setIsShown] = useState(false);


    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd(event) {
        if (!text){
            return;
        }
        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');
    }


    function Note(props) {
        return (
        <div 
            variant = 'light' 
            className='rounded-pill' 
            onMouseEnter={() => setIsShown(true)}
            onMouseLeave={() => setIsShown(false)}
        >
            {props.intext}
            {isShown && (
                <div className="btn-delete-container" onClick={() => props.deleteNote(props.id)}>
                    <Trash />
                </div>
            )}
        </div>
        )
    }


    const deleteNote = (id) => {
        setNote(notes.filter((note) => note.id !== id))
    }

    const handleKeyPress = (event) => {
        if(event.key === 'Enter'){
          if (!text){
            return;
        }

        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');
        }
    }


    return ( 
        <>
            <div className="main-header-text"> 
                <p>Letâ€™s find our ikigai! <br /><br /> Start by adding activites or values you are currently doing into each of these four quadrants.
                <br></br>Feel free to add as many as you can think of! 
                </p>
                <div className="main-logo">
                    <img src={logo} alt="image"/>
                </div> 
            </div>

            <div className="container">
                <div className="rectangle-container text-center">
                    <h3 className="dark-blue">
                        What the world <br></br><b>NEEDS</b>

                        <OverlayTrigger
                            key="bottom"
                            placement="bottom"
                            overlay={
                                <Tooltip id={`tooltip-bottom`}>
                                Tooltip on <strong>bottom</strong>.
                                </Tooltip>
                            }
                        >
                            <Button variant="secondary">i</Button>
                        </OverlayTrigger>
                    </h3>

                    <Container className="pill-container">
                        {notes.map((element) => <Note key = {element.id.toString()} intext={element.intext} id={element.id} deleteNote={deleteNote}></Note>)}
                    </Container>

                    <Container>
                        <Form.Control className='form rounded-pill' value={text} onChange={handleChange} onBlur={handleAdd} onKeyPress={handleKeyPress} placeholder="Type here..."/>
                    </Container>
                </div>
            </div>
        </>
    );
}

export default Far;
